CREATE DATABASE dashboard_db;




CREATE TABLE stats (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(50),
    value INT
);



INSERT INTO stats (name, value) VALUES
('Users', 120),
('Sales', 75),
('Visitors', 300);



USE dashboard_db;
SHOW TABLES;
DESCRIBE stats; -- Shows stats table summary
EXIT

